DECLARE @MainCategory BIGINT = (SELECT Id FROM ReferenceCategory WHERE shortLabel = 'MainCategory')
DECLARE @SecondCategory BIGINT = (SELECT Id FROM ReferenceCategory WHERE shortLabel = 'SecondCategory')
DECLARE @Product BIGINT = (SELECT Id FROM ReferenceCategory WHERE shortLabel = 'Product')

IF NOT EXISTS (SELECT * FROM ReferenceItem WHERE ReferenceCategoryId = @MainCategory OR ReferenceCategoryId= @SecondCategory OR ReferenceCategoryId= @Product)
BEGIN

IF OBJECT_ID('tempdb..#ProductInfo') IS NOT NULL 
BEGIN 
    DROP TABLE #ProductInfo
END

CREATE TABLE #ProductInfo(
	[ID_PRODUCT] [float] NOT NULL,
	[ID_SHOP] [int] NOT NULL,
	[ID_LANG] [int] NOT NULL,
	[description] [nvarchar](max) NULL,
	[description_short] [nvarchar](max) NULL,
	[link_rewrite] [varchar](128) NOT NULL,
	[meta_description] [varchar](255) NULL,
	[meta_keywords] [varchar](255) NULL,
	[meta_title] [varchar](128) NULL,
	[name] [varchar](128) NOT NULL,
	[available_now] [varchar](255) NULL,
	[available_later] [varchar](255) NULL)



IF OBJECT_ID('tempdb..#ProductDescription') IS NOT NULL 
BEGIN 
    DROP TABLE #ProductDescription
END

CREATE TABLE #ProductDescription(
	[ID] [float] NULL,
	[Image] [nvarchar](255) NULL,
	[Nom] [nvarchar](255) NULL,
	[Reference] [float] NULL,
	[Category] [nvarchar](255) NULL,
	[Prix] [nvarchar](255) NULL,
	[PrixFinal] [nvarchar](255) NULL,
	[Quantity] [float] NULL,
	[Etat] [float] NULL
) 

