/* Step1: insert the MainCategory */
INSERT INTO ReferenceItem (Code,ReferenceCategoryId)
SELECT DISTINCT REPLACE(pd.[Category], ' ', '_') , @MainCategory
FROM #ProductDescription pd

/* Step2: insert the SecondCategory */

INSERT INTO ReferenceItem (Code,ReferenceCategoryId,ParentId)
SELECT DISTINCT REPLACE(pd.[Category], ' ', '_') , @SecondCategory, rip.Id
FROM #ProductDescription pd
INNER JOIN ReferenceItem rip ON REPLACE(pd.[Category], ' ', '_') = rip.Code
WHERE rip.ReferenceCategoryId = @MainCategory

/* Step 3: insert the label of MainCategory & SecondCategory(3 languages) */

INSERT INTO ReferenceLabel (ReferenceItemId,Label,Lang)
SELECT distinct ri.Id, pd.[Category] , 'fr'
FROM ReferenceItem ri 
INNER JOIN #ProductDescription pd on ri.Code = REPLACE(pd.[Category], ' ', '_')

INSERT INTO ReferenceLabel (ReferenceItemId,Label,Lang)
SELECT distinct ri.Id, pd.[Category] , 'cn'
FROM ReferenceItem ri 
INNER JOIN #ProductDescription pd on ri.Code = REPLACE(pd.[Category], ' ', '_')

INSERT INTO ReferenceLabel (ReferenceItemId,Label,Lang)
SELECT distinct ri.Id, pd.[Category] , 'en'
FROM ReferenceItem ri 
INNER JOIN #ProductDescription pd on ri.Code = REPLACE(pd.[Category], ' ', '_')



/* Step 4 : Insert the product */
INSERT INTO ReferenceItem (Code,Validity,ReferenceCategoryId,ParentId)
SELECT DISTINCT REPLACE(pd.[Reference], ' ', '_') , pd.[Etat], @Product, rip.Id
FROM #ProductDescription pd
INNER JOIN ReferenceItem rip ON REPLACE(pd.[Category], ' ', '_') = rip.Code
WHERE rip.ReferenceCategoryId = @SecondCategory

/* Step 5: Insert the product label(3 languages) */
INSERT INTO ReferenceLabel (ReferenceItemId,Label,Lang)
SELECT distinct ri.Id, pd.[Nom] , 'en'
FROM ReferenceItem ri 
INNER JOIN #ProductDescription pd on ri.Code = REPLACE(pd.[Reference], ' ', '_')

